class BootstrapGallery
{
    /**
     * Generated by Gallery Plugin
     * @param opts
     * @constructor
     */
    defaultGallery(opts){
        require('bootstrap-sass');
        require('./jquery.bsPhotoGallery');

        opts = (typeof opts !== "object") ? {} : opts;
        opts.tag = opts.tag || 'first';

        $(opts.tag).bsPhotoGallery({
            "classes" : "col-md-5 col-sm-3 col-xs-4 col-xxs-12",
            "hasModal" : true,
        });

    }
}

export { BootstrapGallery }